module.exports = (function () {
    var ExpressionStatement;
    ExpressionStatement = function (expr) {
        return function () {
            if (Object.prototype.toString.call(expr) === '[object Array]' || expr.type === 'ExpressionStatement') {
                return expr;
            } else {
                return [{
                        type: 'ExpressionStatement',
                        expression: expr
                    }];
            }
        }();
    };
    return ExpressionStatement;
}());