function ast(val, context) {
  var result;

  if (val == null) {
    return null;
  } else if (typeof val === 'string') {
    result = { type: 'Identifier', name: val };
  } else if (val.compile) {
    result = val.compile(context);
  } else {
    result = val;
  }

  return result;
}

module.exports = ast;
